<script>
   $(document).ready(function() {
      $('#AuthorId').select2();
      $('#PublisherId').select2();
      $('#CategoryIDs').select2();
      $('#SummernoteDescription').summernote({
          height: 150
      });
      $('#active-book').bootstrapToggle({
           on: 'Active',
